private const val UNIQUE_ID_CHARS = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789"

fun createUniqueName(): String
{
	var name = "Server-"
	for (i in 0 ..< 10)
	{
		name += UNIQUE_ID_CHARS.random()
	}
	return name
}

